"use strict";

let gulp = require("gulp"),
    lobster = require("../lib/lobster"),
    opts = {
        apiKey: process.env.CODE_COVERAGE_API_KEY
    };

module.exports = function () {
    return lobster.getIstanbulDefaultPathStream()
    .pipe(lobster.sendIstanbulCoverageInfo(opts))
    .pipe(gulp.dest("logs"));
};
